        'isBlogAuthed': isBlogAuthed,
        'getOauthUrl': getOauthUrl,
        'isMaster': isMaster,
        'isUrlAccessibleForUser': isUrlAccessibleForUser,
        'getLoginUrl': getLoginUrl,
